export let changeOneIntoAnother = [
  // the sign will be changed from positive to negative or vice versa for:
  // 1) the expression not preceded by operators (minus here indicates negative value of the following number rather than an operator sign)
  {
    before: "\\(",
    afterChangeSign: "( - ",
    afterAbs: "(abs(",
    afterAfterDenomination: "((1 / (",
  },
  {
    before: "\\( - ",
    afterChangeSign: "(",
    afterAbs: "(abs( - ",
    afterAfterDenomination: "((1 / ( - ",
  },
  {
    before: "^",
    afterChangeSign: " - ",
    afterAbs: "abs(",
    afterAfterDenomination: "(1 / (",
  },
  {
    before: "^ - ",
    afterChangeSign: "",
    afterAbs: "abs( - ",
    afterAfterDenomination: "(1 / ( - ",
  },

  // 2) the expression preceded by two consecutive operators:
  {
    before: " -  - ",
    afterChangeSign: " - ",
    afterAbs: " - abs( - ",
    afterAfterDenomination: " - (1 / ( - ",
  },
  {
    before: " \\+  - ",
    afterChangeSign: " + ",
    afterAbs: " + abs( - ",
    afterAfterDenomination: " + (1 / ( - ",
  },
  {
    before: " \\*  - ",
    afterChangeSign: " * ",
    afterAbs: " * abs( - ",
    afterAfterDenomination: " * (1 / ( - ",
  },
  {
    before: " \\/  - ",
    afterChangeSign: " / ",
    afterAbs: " / abs( - ",
    afterAfterDenomination: " / (1 / ( - ",
  },
  {
    before: " \\^  - ",
    afterChangeSign: " ^ ",
    afterAbs: " ^ abs( - ",
    afterAfterDenomination: " ^ (1 / ( - ",
  },
  {
    before: " yroot  - ",
    afterChangeSign: " yroot ",
    afterAbs: " yroot abs( - ",
    afterAfterDenomination: " yroot (1 / ( - ",
  },
  {
    before: " mod  - ",
    afterChangeSign: " mod ",
    afterAbs: " mod abs( - ",
    afterAfterDenomination: " mod (1 / ( - ",
  },
  // 3) the expression preceded by only one operator:
  {
    before: " - ",
    afterChangeSign: " + ",
    afterAbs: " - abs(",
    afterAfterDenomination: " - (1 / (",
  },
  {
    before: " \\+ ",
    afterChangeSign: " - ",
    afterAbs: " + abs(",
    afterAfterDenomination: " + (1 / (",
  },
  {
    before: " \\* ",
    afterChangeSign: " *  - ",
    afterAbs: " * abs(",
    afterAfterDenomination: " * (1 / (",
  },
  {
    before: " \\/ ",
    afterChangeSign: " /  - ",
    afterAbs: " / abs(",
    afterAfterDenomination: " / (1 / (",
  },
  {
    before: " \\^ ",
    afterChangeSign: " ^  - ",
    afterAbs: " ^ abs(",
    afterAfterDenomination: " ^ (1 / (",
  },
  {
    before: " yroot ",
    afterChangeSign: " yroot  - ",
    afterAbs: " yroot abs(",
    afterAfterDenomination: " yroot (1 / (",
  },
  {
    before: " log base ",
    afterChangeSign: " log base ", // no changes
    afterAbs: " log base abs(",
  },
  {
    before: " mod ",
    afterChangeSign: " mod ",
    afterAbs: " mod abs(",
  },
];
