  }

  for (let i = 0; i < arr.length; i++) {
    if (arr[i] !== null) {
      finalDisplayOpsChangeSign = displayOps.slice(0, arr[i].index).concat(changeOneIntoAnother[i]["after"]).concat(Math.abs(state.displayCur));
      // console.log("finalDisplayOpsChangesign", finalDisplayOpsChangeSign);
      break;
    }
  }
  return finalDisplayOpsChangeSign;
}

function findExpression(state) {
