import React from "react";
import "./styles/styles.css";
import $ from "jquery";
import calculate from "./functions";

export default class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      displayOps: "",
      result: [],
      displayCur: "",
      lastInput: "",
    };
    this.handleClear = this.handleClear.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
    this.handleDigit = this.handleDigit.bind(this);
    this.handleEquals = this.handleEquals.bind(this);
  }

  handleClear() {
    this.setState({
      displayOps: "",
      result: [],
      displayCur: 0,
      lastInput: "",
    });
  }

  handleAdd() {
    if (this.state.lastInput !== "+") {
      this.setState(
        (state) => ({
          displayOps: state.displayOps.concat("+"),
          result: state.result.concat(Number(state.displayCur)).concat("+"),
          displayCur: "",
          lastInput: "+",
        }),
        () => {
          // console.log("inside handleAdd", this.state.result);
        }
      );
    }
  }

  handleDigit(e) {
    console.log("e.target.value", e.target.value);
    this.setState((state) => ({
      displayOps: state.displayOps.concat("7"),
      displayCur: state.displayCur === 0 ? "7" : state.displayCur.concat("7"),
      lastInput: "7",
    }));
  }

  handleEquals() {
    this.setState(
      (state) => ({
        result: state.result.concat(Number(state.displayCur)),
      }),
      () => {
        let result = calculate(this.state.result);
        this.setState((state) => ({
          displayOps: state.displayOps.concat("=").concat(result),
          displayCur: result,
        }));
        console.log("result inside handleEquals", result);
      }
    );
  }

  render() {
    return (
      <>
        <Display ops={this.state.displayOps} cur={this.state.displayCur} />
        <Buttons clear={this.handleClear} add={this.handleAdd} digit={this.handleDigit} equals={this.handleEquals} />
        <Footer />
      </>
    );
  }
}

class Display extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <>
        <div id="display">
          <div id="displayRes">{this.props.ops}</div>
          <div id="displayOps">{this.props.cur}</div>
        </div>
      </>
    );
  }
}

class Buttons extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <>
        <button id="clear" onClick={this.props.clear}>
          AC
        </button>
        <button id="add" onClick={this.props.add}>
          +
        </button>
        <button id="seven" value="7" onClick={this.props.digit}>
          7
        </button>
        <button id="eight">8</button>
        <button id="nine">9</button>
        <button id="subtract">-</button>
        <button id="four">4</button>
        <button id="five">5</button>
        <button id="six">6</button>
        <button id="multiply">*</button>
        <button id="one">1</button>
        <button id="two">2</button>
        <button id="three">3</button>
        <button id="divide">/</button>
        <button id="zero">0</button>
        <button id="decimal">.</button>
        <button id="equals" onClick={this.props.equals}>
          =
        </button>
      </>
    );
  }
}

function Footer() {
  return (
    <footer>
      <div id="footer">This project was build using: HTML, CSS, JavaScript, React, Redux, jQuery, Bootstrap and SASS</div>
    </footer>
  );
}
