import { expression, expressionNegative, findExpression } from "./findExpression";
import calculate from "./equals";

let stateForFactorial = "";
export function factorialState(state) {
  stateForFactorial = state;
}

export function factorial(num) {
  let result = "";
  if (num < 0) {
    // if (stateForFactorial.factorialAlert) {
    //   console.log("you are in factorialAlert section")
    alert("invalid input: factorials are only defined for positive numbers");
    //   }
    // HERE PLACE CODE TO BLOCK ALL BUTTONS EXCEPT FOR ac and del (consider also numbers, specials numbers, opening brace, random number, decimal, minus --- better just ac and del)
    return "invalid input";
  } else if (num % 1 !== 0 && num !== ")") {
    validInput(stateForFactorial);
    return Math.pow(2 * num * Math.PI, 1 / 2) * Math.pow(num / Math.E, num);
  } else if (num === ")") {
    return "!";
  } else {
    result = Number(num);
    if (Number.isInteger(result)) {
      for (let i = result - 1; i > 0; i--) {
        result *= i;
      }
      return result;
    }
  }
}

export function validInput(state) {
  findExpression(state);
  let result = calculate(state, expression);
  console.log("you are in factorial: RESULT", result, "EXPRESSION", expression, expressionNegative);
  if (result < 0) {
    return "invalid input";
  } else if (result % 1 !== 0) {
    alert(`factorials for nonintegers are defined based on simplified Gamma function:
      ~~ level of accuracy: low ~~`);
  }
}
