export default function calculate(arr) {
  let mulInd = arr.findIndex((elem) => elem === "x");
  console.log("arr", arr);
  if (mulInd !== -1) {
    let currentOperation = arr.slice(mulInd - 1, mulInd + 2);
    let currentResult = currentOperation[0] * currentOperation[2];

    let result = arr
      .slice(0, mulInd - 1)
      .concat(currentResult)
      .concat(arr.slice(mulInd + 2));

    return calculate(result);
  }
  arr = calculateSameOperations(arr, "+");
  console.log("arr just after calcSame inside main fun", arr);
  // let operatorIndex = arr.findIndex((elem) => elem === "+");
  // if (operatorIndex !== -1) {
  //   return calculateSameOperations(arr, "+");
  // }
  // operatorIndex = arr.findIndex((elem) => elem === "-");
  // if (operatorIndex !== -1) {
  //   return calculateSameOperations(arr, "-");
  // }

  return arr[0];
}

function calculateSameOperations(arr, operator) {
  console.log("insede SameOperations", arr, operator);
  let operatorIndex = arr.findIndex((elem) => elem === operator);
  if (operatorIndex !== -1) {
    let currentOperation = arr.slice(operatorIndex - 1, operatorIndex + 2);
    let currentResult = operator === "+" ? currentOperation[0] + currentOperation[2] : currentOperation[0] - currentOperation[2];

    let result = arr
      .slice(0, operatorIndex - 1)
      .concat(currentResult)
      .concat(arr.slice(operatorIndex + 2));

    return calculateSameOperations(result, operator);
  }
  console.log("insede SameOperations before return", arr, operator);
  return arr;
}
