export let changeOneIntoAnother = [
  // the sign will be changed from positive to negative or vice versa for:
  // 1) the expression not preceded by operators (minus here indicates negative value of the following number rather than an operator sign)
  {
    before: "\\(",
    afterChangeSign: "( - ",
    afterAbs: "(abs(",
  },
  {
    before: "\\( - ",
    afterChangeSign: "(",
    afterAbs: "(abs( - ",
  },
  {
    before: "^",
    afterChangeSign: " - ",
    afterAbs: "abs(",
  },
  {
    before: "^ - ",
    afterChangeSign: "",
    afterAbs: "abs( - ",
  },

  // 2) the expression preceded by two consecutive operators:
  {
    before: " -  - ",
    afterChangeSign: " - ",
    afterAbs: " - abs( - ",
  },
  {
    before: " \\+  - ",
    afterChangeSign: " + ",
    afterAbs: " + abs( - ",
  },
  {
    before: " \\*  - ",
    afterChangeSign: " * ",
    afterAbs: " * abs( - ",
  },
  {
    before: " \\/  - ",
    afterChangeSign: " / ",
    afterAbs: " / abs( - ",
  },
  {
    before: " \\^  - ",
    afterChangeSign: " ^ ",
  },
  {
    before: " yroot  - ",
    afterChangeSign: " yroot ",
  },
  {
    before: " mod  - ",
    afterChangeSign: " mod ",
  },
  // 3) the expression preceded by only one operator:
  {
    before: " - ",
    afterChangeSign: " + ",
  },
  {
    before: " \\+ ",
    afterChangeSign: " - ",
  },
  {
    before: " \\* ",
    afterChangeSign: " *  - ",
  },
  {
    before: " \\/ ",
    afterChangeSign: " /  - ",
  },
  {
    before: " \\^ ",
    afterChangeSign: " ^  - ",
  },
  {
    before: " yroot ",
    afterChangeSign: " yroot  - ",
  },
  {
    before: " mod ",
    afterChangeSign: " mod ",
  },
];
