export default function calculate(arr) {
  let mulInd = arr.findIndex((elem) => elem === "x");
  console.log("arr", arr);
  if (mulInd !== -1) {
    let currentOperation = arr.slice(mulInd - 1, mulInd + 2);
    let currentResult = currentOperation[0] * currentOperation[2];

    let result = arr
      .slice(0, mulInd - 1)
      .concat(currentResult)
      .concat(arr.slice(mulInd + 2));

    return calculate(result);
  }

  let addInd = arr.findIndex((elem) => elem === "+");
  if (addInd !== -1) {
    let currentOperation = arr.slice(addInd - 1, addInd + 2);
    let currentResult = currentOperation[0] + currentOperation[2];

    let result = arr
      .slice(0, addInd - 1)
      .concat(currentResult)
      .concat(arr.slice(addInd + 2));

    return calculate(result);
  }

  calculateSameOperations();

  return arr[0];
}

function calculateSameOperations(operator) {
  let subInd = arr.findIndex((elem) => elem === "-");
  if (subInd !== -1) {
    let currentOperation = arr.slice(subInd - 1, subInd + 2);
    let currentResult = currentOperation[0] - currentOperation[2];

    let result = arr
      .slice(0, subInd - 1)
      .concat(currentResult)
      .concat(arr.slice(subInd + 2));

    return calculateSameOperations(result);
  }
}
