export default function calculate(arr) {
  arr = calcIdenticalOperations(arr, "*", "/");
  // arr = calcIdenticalOperations(arr, "/");
  arr = calcIdenticalOperations(arr, "+");
  arr = calcIdenticalOperations(arr, "-");

  return arr[0];
}

function calcIdenticalOperations(arr, operator1, operator2) {
  console.log("insede SameOperations", arr, operator1, operator2);
  let operatorIndex = arr.findIndex((elem) => elem === operator1 || operator2);
  let operator = arr[operatorIndex];
  if (operatorIndex !== -1) {
    let currentOperation = arr.slice(operatorIndex - 1, operatorIndex + 2);
    let currentResult = operator === "*" ? currentOperation[0] * currentOperation[2] : operator === "/" ? currentOperation[0] / currentOperation[2] : operator === "+" ? currentOperation[0] + currentOperation[2] : currentOperation[0] - currentOperation[2];

    let result = arr
      .slice(0, operatorIndex - 1)
      .concat(currentResult)
      .concat(arr.slice(operatorIndex + 2));

    return calcIdenticalOperations(result, operator1, operator2);
  }
  console.log("insede SameOperations before return", arr, operator1, operator2);
  return arr;
}
